void private_cweb_interpret_query_params(struct CwebHttpRequest *self,const char *query_params){
    int paramns_size = strlen(query_params);
    char key[1000] = {0};
    char value[1000] = {0};
    bool key_found = false;

    for(int i=1; i<paramns_size; i++){

        if(query_params[i] == '='){
            key_found = true;
            continue;
        }

        if(query_params[i] == '&'){
            key_found = false;
            self->params->set(self->params, key, value);
            memset(key, 0, 1000);
            memset(value, 0, 1000);
            continue;
        }
        
        if(key_found){
            value[strlen(value)] = query_params[i];
        }
        
        else{
            key[strlen(key)] = query_params[i];
        }
    }
    if(key_found){
        self->params->set(self->params, key, value);
    }

}
void private_cweb_interpret_first_line(struct CwebHttpRequest *self, char *first_line){
    
    char method[1000] = {0};
    char url[1000] = {0};

    sscanf(first_line, "%s %s", method, url);
    
    
    self->method = (char*)malloc(strlen(method)+2);
    strcpy(self->method, method);

    
    self->url = (char*)malloc(strlen(url)+2);
    strcpy(self->url, url);
    
    char route[1000] = {0};
    char params[30000] = {0};

    sscanf(url, "%[^?]%s", route, params);

    self->route = (char*)malloc(strlen(route)+1);
    strcpy(self->route, route);

    self->interpret_query_params(self, params);
    

    
}


void private_cweb_interpret_headders(struct CwebHttpRequest *self,struct DtwStringArray *line_headers){
    
    for(int i = 1;i< line_headers->size;i++){
        char *current_line = line_headers->strings[i];
        int line_size = strlen(current_line);
        char key[1000] = {0};
        char value[3000] = {0};
        bool key_found = false;
        int value_start_point = 0;
        for(int j = 0; j<line_size;j++){
            
            if(current_line[j] == ':' && key_found == false){
                key_found = true;
                j+=1;
                value_start_point = j;
                continue;
            }

            if(key_found == false){
                key[j] = current_line[j];
            }

            if(key_found == true){
                int size_point = j - value_start_point -1;
               value[size_point] = current_line[j];
            }
        }

        if(key_found){
            self->headers->set(self->headers, key, value);

        }
    }

}

struct CwebHttpRequest *private_cweb_create_http_request(char *raw_entrys){
        //splite lines by "\r\n"

    struct CwebHttpRequest *self = private_cweb_request_constructor();
    self->raw_entrys = raw_entrys;
    
    struct DtwStringArray *lines = dtw_constructor_string_array();

    char last_string[10000]= {0};
    int line_index = 0;
    int i = 0;
    
    while (true){


        if(
            raw_entrys[i]  == '\r' &&
            raw_entrys[i+1] == '\n' &&
            raw_entrys[i+2] == '\r' &&
            raw_entrys[i+3] == '\n'
        ){
            last_string[line_index] = '\0';
            lines->add_string(lines, last_string);
            break;
        }
        //means its an break line        
        if (raw_entrys[i] == '\r' && raw_entrys[i+1] == '\n'){
            last_string[line_index] = '\0';
            lines->add_string(lines, last_string);
            line_index=0;
            i++;
        }

        else{
            last_string[line_index] = raw_entrys[i];
            line_index++;
        }    
        i++;

    }
    self->interpret_first_line(self, lines->strings[0]);
    self->interpret_headders(self, lines);

    

    char *content_lenght_str = self->headers->get_value(self->headers, "Content-Length");
    if(content_lenght_str != NULL){
        self->content_length = atoi(content_lenght_str);
        self->content = (unsigned char *)malloc(self->content_length);
        int content_start = i+4;
        for(int j = 0; j<self->content_length; j++){
            self->content[j] = raw_entrys[content_start+j];
        }
        //extracting url encoded data
        char *content_type = self->headers->get_value(self->headers, "Content-Type");
        if(content_type != NULL){
            if(strcmp(content_type, "application/x-www-form-urlencoded") == 0){
                self->interpret_query_params(self, (char*)self->content);
            }
        }
    }


    lines->free_string_array(lines);
    return self;
}


void private_cweb_represent_http_request(struct CwebHttpRequest *self){
    
    printf("url: %s\n", self->url);
    printf("route: %s\n", self->route);
    printf("method: %s\n", self->method);
    printf("params:-----------------------------\n");
    self->params->represent(self->params);
    printf("headers:----------------------------\n");
    self->headers->represent(self->headers);
    printf("content_length: %d\n", self->content_length);
    printf("content: %s\n", self->content);

}

void private_cweb_free_http_request(struct CwebHttpRequest *self){
    if(self->url != NULL){
        free(self->url);
    }
    if(self->route != NULL){
        free(self->route);
    }
    if(self->method != NULL){
        free(self->method);
    }
    if(self->content != NULL){
        free(self->content);
    }
    self->params->free(self->params);

    self->headers->free(self->headers);
    free(self);

}



struct CwebHttpResponse* cweb_send_any(const char *content_type,size_t content_length,unsigned char *content,int status_code){
    struct CwebHttpResponse *response = create_http_response();
    response->add_header(response, "Content-Type", content_type);
    response->set_content(response, content, content_length);
    response->status_code = status_code;
    return response;
}


struct CwebHttpResponse* cweb_send_text(const char *content,int status_code){
    return cweb_send_any("text/plain", strlen(content), (unsigned char*)content, status_code);
}


struct CwebHttpResponse* cweb_send_file(const char *file_path,const char *content_type,int status_code){
    
    int size = 0;
    unsigned char *content = dtw_load_binary_content(file_path, &size);
   
    if(content == NULL){
        char *mensage = (char*)malloc(100);
        sprintf(mensage, "File not found: %s", file_path);
        struct CwebHttpResponse* response =  cweb_send_text(mensage, CWEB_NOT_FOUND);
        free(mensage);
        return response;
    }


    struct DtwPath *path = dtw_constructor_path(file_path);
    char *content_type_created = (char*)malloc(100);
    if(content_type == NULL){
        char *extension = path->get_extension(path);
        if(strcmp(extension, "html") == 0){
            strcpy(content_type_created, "text/html");
        }
        else if(strcmp(extension, "css") == 0){
            strcpy(content_type_created, "text/css");
        }
        else if(strcmp(extension, "js") == 0){
            strcpy(content_type_created, "text/javascript");
        }
        else if(strcmp(extension, "png") == 0){
            strcpy(content_type_created, "image/png");
        }
        else if(strcmp(extension, "jpg") == 0){
            strcpy(content_type_created, "image/jpeg");
        }
        else if(strcmp(extension, "jpeg") == 0){
            strcpy(content_type_created, "image/jpeg");
        }
        else{
            strcpy(content_type_created, "text/plain");
        }
        free(extension);
    }
    else{
        strcpy(content_type_created, content_type);
    }

    return cweb_send_any(content_type_created, size+1, content, status_code);
    
}


struct CwebKeyVal* cweb_key_val_constructor(const char *key,const  char *value){
    struct CwebKeyVal *self = (struct CwebKeyVal*)malloc(sizeof(struct CwebKeyVal));
    self->key = (char*)malloc(strlen(key)+1);
    strcpy(self->key,key);
    self->value = (char*)malloc(strlen(value)+1);
    strcpy(self->value,value);
    self->represent = private_cweb_represent_key_val;
    self->free = private_cweb_free_key_val;
    return self;
}

void private_cweb_represent_key_val(struct CwebKeyVal *self){
    printf("%s : %s\n",self->key,self->value);
}

void private_cweb_free_key_val(struct CwebKeyVal *self){
    free(self->key);
    free(self->value);
    free(self);
}





struct CwebDict *cweb_create_dict(){
    struct CwebDict *self = (struct CwebDict *)malloc(sizeof(struct CwebDict));
    self->keys_vals = (struct CwebKeyVal**)malloc(1);
    self->size = 0;
    self->set = private_cweb_dict_set;
    self->get_value = private_cweb_dict_get;
    self->free = private_cweb_free_dict;
    self->represent = private_cweb_dict_represent;
    return self;
}

void private_cweb_dict_set(struct CwebDict *self,const char *key,const char *value){
    struct CwebKeyVal *key_val = cweb_key_val_constructor(key, value);
    self->keys_vals = (struct CwebKeyVal**)realloc(self->keys_vals, (self->size+1)*sizeof(struct CwebKeyVal*));
    self->keys_vals[self->size] = key_val;
    self->size++;
}

char * private_cweb_dict_get(struct CwebDict *self,const  char *key){
    for(int i = 0; i < self->size; i++){
        if(strcmp(self->keys_vals[i]->key, key) == 0){
            return self->keys_vals[i]->value;
        }
    }
    return NULL;
}

void private_cweb_dict_represent(struct CwebDict *self){
    for(int i = 0; i < self->size; i++){
        self->keys_vals[i]->represent(self->keys_vals[i]);
    }
}

void private_cweb_free_dict(struct CwebDict *self){
    
    for(int i = 0; i < self->size; i++){
        struct CwebKeyVal *key_val = self->keys_vals[i];
         key_val->free(key_val);
    }
    free(self->keys_vals);
    free(self);
}


struct CwebHttpResponse *create_http_response(){
    struct CwebHttpResponse *self = (struct CwebHttpResponse *)malloc(
        sizeof(struct CwebHttpResponse)
    );
    self->status_code = 200;
    self->headers = cweb_create_dict();
    self->content_length = 0;
    self->exist_content = false;
    self->content = NULL;
    self->free = private_cweb_http_response_free;
    self->set_content = private_cweb_http_set_content;
    self->generate_response = private_cweb_generate_response;
    self->add_header = private_cweb_http_add_header;
    return self;
}

char *private_cweb_generate_response(struct CwebHttpResponse*self){
    char *response_string = (char*)malloc(1000);
    sprintf(response_string, "HTTP/1.1 %d OK\r\n", self->status_code);
    struct CwebDict *headers = self->headers;
    char content_length_str[1000] = {0};
    sprintf(content_length_str, "Content-Length: %d\r\n",self->content_length);
    strcat(response_string, content_length_str);

    for(int i = 0; i < headers->size; i++){
        struct CwebKeyVal *key_val = headers->keys_vals[i];
        char *key = key_val->key;
        char *value = key_val->value;
        char header[1000] = {0};
        sprintf(header, "%s: %s\r\n", key, value);
        strcat(response_string, header);
    }
    if(self->headers->size == 0){
        strcat(response_string, "\r\n");
    }
    strcat(response_string, "\r\n");
        
    return response_string;
}

void private_cweb_http_response_free(struct CwebHttpResponse *self){
    self->headers->free(self->headers);
    free(self->content);
    free(self);
}

void private_cweb_http_set_content(struct CwebHttpResponse *self, unsigned char *content,int content_length){
    self->content = (unsigned char*)malloc(content_length +2);
    memcpy(self->content, content, content_length);
    self->exist_content = true;
    self->content_length = content_length;
}

void private_cweb_http_add_header(struct CwebHttpResponse *self,const char *key,const  char *value){
    self->headers->set(self->headers, key, value);
}



void  private_cweb_execute_request(
        int new_socket,
        size_t max_request_size,
        struct CwebHttpResponse*(*request_handler)( struct CwebHttpRequest *request)
){
        char *buffer = (char*)malloc(max_request_size);

        // Lendo a solicitao HTTP do cliente

        cweb_print("Reading request\n");
        int valread = read(new_socket, buffer, max_request_size);


    //check if the request is valid
        if(valread <= 0){
            cweb_print("Error Reading request \n");
            free(buffer);
            return;
        }

        cweb_print("Executing lambda\n");
        struct CwebHttpRequest *request  = private_cweb_create_http_request(
                buffer
        );
        cweb_print("created request\n");
        cweb_print("Request method: %s\n",request->method);
        cweb_print("Request url: %s\n",request->url);

        struct CwebHttpResponse *response;
        response = request_handler(request);
        cweb_print("executed client lambda\n");        
       
        if(response == NULL){
            response = cweb_send_text(
                "Error 404",
                404
            );
        };
        
        char *response_str = response->generate_response(response);
        cweb_print("Response created\n");


        send(new_socket, response_str,strlen(response_str) ,MSG_NOSIGNAL);

        if(response->exist_content){
            send(new_socket, response->content, response->content_length, MSG_NOSIGNAL);
        }

        free(response_str);
        response->free(response);
        request->free(request);
        free(buffer);
        cweb_print("Cleared memory\n");
        return ;
}


void private_cweb_send_error_mensage(int new_socket){
   
    struct CwebHttpResponse *response = cweb_send_text(
        "Error 500 Internal Server Error",
        500
    );
    char *response_str = response->generate_response(response);
    send(new_socket, response_str,strlen(response_str) , 0);
    send(new_socket, response->content, response->content_length, 0);

    free(response_str);
    response->free(response);
    
}
